            void InitializeDataView()
            {

                #region Relations

                Relations.Add(EmployerSessionDet,
                        EmployerSessionDet.Employer_Ref.BindEqualTo(v_EmployerReference).And(
                        EmployerSessionDet.Run_By_Calculation.BindEqualTo(true)),
                    EmployerSessionDet.SortByByLastCalcSession);
                Relations[EmployerSessionDet].NotifyRowWasFoundTo(v_CalculationsRun);

                Relations.Add(CustomerFormatTypes,
                        CustomerFormatTypes.Customer_Format_Type.BindEqualTo("B").And(
                        CustomerFormatTypes.Customer_Format_Code.BindEqualTo(v_ManeBACS_type)),
                    CustomerFormatTypes.SortByByType_Letter_PK);
                Relations[CustomerFormatTypes].BindEnabled(() => Exp_170());

                Relations.Add(EmployerPeriodDets,
                        EmployerPeriodDets.Employer_Ref.BindEqualTo(v_EmployerReference).And(
                        EmployerPeriodDets.Tax_Year.BindEqualTo(v_TaxYear)).And(
                        EmployerPeriodDets.Tax_Period.BindEqualTo(v_PeriodNumber)),
                    EmployerPeriodDets.SortByByPeriod_PK);

                Relations.Add(EmployerReference,
                        EmployerReference.Employer_Ref.BindEqualTo(v_EmployerReference),
                    EmployerReference.SortByByEmployer_PK);
                Relations[EmployerReference].NotifyRowWasFoundTo(RC_employer);

                Relations.Add(Employer_Reference_2,
                        Employer_Reference_2.Employer_Ref.BindEqualTo(v_EmployerReference),
                    Employer_Reference_2.SortByByEmployer_Ref_PK);

                Relations.Add(TempestControlFile,
                        TempestControlFile.Key_Field.BindEqualTo("BACS USER NAME"),
                    TempestControlFile.SortByByKeyField_PK);
                Relations[TempestControlFile].BindEnabled(() => v_Adecco_Eire);

                Relations.Add(TempestControlFile1,
                        TempestControlFile1.Key_Field.BindEqualTo(() => "QSTD" + EmployerSessionDet.Employer_Ref + "          " + u.Str(EmployerSessionDet.Tax_Year, "4") + u.If(Employer_Reference_2.Use_RTI, u.Str(EmployerPeriodDets.Tax_Period, "3"), "")).And(
                        TempestControlFile1.Logical_Value.BindEqualTo(true)),
                    TempestControlFile1.SortByByKeyField_PK);
                Relations[TempestControlFile1].NotifyRowWasFoundTo(RC_QualityStdRunForPeriod);

                Relations.Add(CustomerFormatTypes1,
                        CustomerFormatTypes1.Customer_Format_Type.BindEqualTo("B").And(
                        CustomerFormatTypes1.Customer_Format_Code.BindEqualTo(_parent.v_GlobalBACS_Type)),
                    CustomerFormatTypes1.SortByByType_Letter_PK);
                Relations[CustomerFormatTypes1].NotifyRowWasFoundTo(rc_GlobalBACS_foundTF);

                Relations.Add(CustomerFormatTypes2,
                        CustomerFormatTypes2.Customer_Format_Type.BindEqualTo("B").And(
                        CustomerFormatTypes2.Customer_Format_Code.BindEqualTo(EmployerReference.BACS_Type)),
                    CustomerFormatTypes2.SortByByType_Letter_PK);
                Relations[CustomerFormatTypes2].NotifyRowWasFoundTo(rc_EmployerBACS_foundTF);

                Relations.Add(Pay_Methods,
                        Pay_Methods.Pay_Method.BindEqualTo(v_PayMethod_VS),
                    Pay_Methods.SortByByPay_Method_PK);
                Relations[Pay_Methods].BindEnabled(() => Exp_191());
                Relations[Pay_Methods].Associate(v_UK);

                Relations.Add(EmployerSessionDet1,
                        EmployerSessionDet1.Employer_Ref.BindEqualTo(v_EmployerReference).And(
                        EmployerSessionDet1.Tax_Year.BindEqualTo(v_TaxYear)).And(
                        EmployerSessionDet1.Tax_Period.BindEqualTo(v_PeriodNumber)).And(
                        EmployerSessionDet1.Tax_Session.BindEqualTo(v_SessionNumber)),
                    EmployerSessionDet1.SortByBySessionNumber_PK);
                Relations[EmployerSessionDet1].NotifyRowWasFoundTo(v_SessionDetsExist);

                Relations.Add(EmployerPeriodDets1,
                        EmployerPeriodDets1.Employer_Ref.BindEqualTo(v_EmployerReference).And(
                        EmployerPeriodDets1.Tax_Year.BindEqualTo(v_TaxYear)),
                    EmployerPeriodDets1.SortByByPeriod_PK);
                Relations[EmployerPeriodDets1].OrderBy.Reversed = true;

                Relations.Add(EmployerPeriodDets2,
                        EmployerPeriodDets2.Employer_Ref.BindEqualTo(v_EmployerReference).And(
                        EmployerPeriodDets2.Tax_Year.BindEqualTo(v_TaxYear)),
                    EmployerPeriodDets2.SortByByPeriod_PK);

                Relations.Add(DivisionCodes,
                        DivisionCodes.Division_Code.BindEqualTo(v_Division),
                    DivisionCodes.SortByByDivisionCode_PK);
                Relations[DivisionCodes].NotifyRowWasFoundTo(rc_92_Division);

                Relations.Add(BankDetails,
                        BankDetails.Bank_Sort_Code.BindEqualTo(DivisionCodes.TS_DIV_Bank_Code),
                    BankDetails.SortByByBankSortCode_PK);

