            protected override void OnLeaveRow()
            {
                AutoError.Value = false;
                // Screen Errors Here...
                if (_parent.IsAutomationMode())
                {
                    if (_parent.ValidateEmployer(v_EmployerReference, RC_employer, v_CalculationsRun, rc_GlobalBACS_foundTF, rc_EmployerBACS_foundTF)) { AutoError.Value = true; }
                    if (_parent.ValidateSession(v_SessionNumber, v_SessionDetsExist, EmployerSessionDet1.Run_By_Calculation)) { AutoError.Value = true; }
                    if (v_FilesToGenerate == "BACS file/FPS")
                    {
                        v_FPS_inOutbox.Value = false;
                        v_FPS_AlreadySent.Value = false;
                        new CheckFPS_inOutbox(this).Run(v_FPS_inOutbox);
                        new CheckFPS_Sent(this).Run(v_FPS_AlreadySent);
                        if (_parent.ValidateFPS(v_FPS_inOutbox, v_FPS_AlreadySent)) { AutoError.Value = false; }
                    }
                    if (_parent.ValidateInterfaceHrdFilename(v_InterfaceHrdFilename, _parent.v_GlobalBACS_Type)) { AutoError.Value = true; }
                    if (_parent.ValidateDivision(v_Division, rc_92_Division)) { AutoError.Value = true; }
                    if (_parent.ValidateProcessingOwner(v_BACS_ProcessingOwner, v_Division, DivisionCodes.TS_BACS_Group_Name, DivisionCodes.TS_DIV_Bank_Code, DivisionCodes.TS_DIV_Bank_Ac_Num, DivisionCodes.TS_BACS_Bureau, DivisionCodes.TS_BACS_User_Number, DivisionCodes.TS_BACS_Interface_File)) { AutoError.Value = true; }
                    if (_parent.ValidateBACS_FilenameLocal(v_InterfaceHrdFilename, v_BACS_FilenameLocal, _parent.v_GlobalBACS_Type)) { AutoError.Value = true; }
                    if (v_BACSFP)
                    {
                        if (_parent.ValidateBACSFP_FilenameLocal(v_BACSFP_FilenameLocal, v_BACS_FilenameLocal)) { AutoError.Value = true; }
                        if (_parent.ValidateBACSFP_PayDay(v_BACSFP_PayDay, EmployerPeriodDets1.Period_Start_Date, EmployerPeriodDets2.Period_End_Date)) { AutoError.Value = true; }
                    }
                }
                // Start Button Code Here...
                if (!AutoError)
                {
                    v_ContinueCreatingBACS.Value = true;
                    if (Application.v_Category_BatchProcessing != 0)
                    {
                        Part079.PopulateVirtualsFromINI.Create().Run(
                                            null,
                                            null,
                                            null,
                                            null,
                                            null,
                                            v_BACS_FilenameLocal,
                                            v_TaxYear.Value,
                                            v_PeriodNumber.Value,
                                            v_SessionNumber.Value);
                        Part079.LogProgramActivity.Create().Run(
                                            u.Trim(u.IniGet("[SCL]ER")) + u.Trim(u.IniGet("[SCL]Function_ID") + u.Trim(u.IniGet("[SCL]Batch_Reference"))),
                                            null,
                                            null,
                                            null,
                                            null,
                                            null,
                                            null,
                                            null,
                                            u.Trim(v_BACS_FilenameLocal),
                                            false);
                    }
                    new CheckFPS_Sent(this).Run(v_FPS_AlreadySent);
                    if (v_FPS_AlreadySent)
                    {
                        AutoError.Value = true;
                        _parent.StartButtonError("BACS file cannot be created as Full Payment Submission sent.");
                    }
                    if (!AutoError)
                    {
                        new CheckFPS_inOutbox(this).Run(v_FPS_inOutbox);
                        if (v_FilesToGenerate == "BACS file only" && v_FPS_inOutbox && !_parent.IsAutomationMode())
                        {
                            Part001.MA_4L_ConfirmPrompt.Create().Run(
                                            v_ContinueCreatingBACS,
                                            "A FPS file has already been created for this period. Your BACS and FPS",
                                            "files may not match. Do you wish to continue?");
                        }
                    }
                    if (v_ContinueCreatingBACS && !AutoError)
                    {
                        new R_W_BACS_OWNER_SETTING(this).Run("W");
                        new GetNextHD_BACS_runNumber(this).Run();
                        v_UnprocessedCHAPS.Value = false;
                        if (Tempest.Roles.SpringIT.Allowed)
                        {
                            new UnprocessedCHAPS_Entries(this).Run();
                        }
                        if (!v_UnprocessedCHAPS)
                        {
                            v_CountOfEmployees.Value = 0;
                            v_TotalAmount.Value = 0;
                            // PBI #27083...
                            v_TotalAmount_Non_UK.Value = 0;
                            u.SetCrsr(2);
                            if (Tempest.Roles.MorganHunt.Allowed)
                            {
                                new GetEmailBodyText(this).Run();
                            }
                            if ((v_BACS_ProcessingOwner == "I" || !_parent.v_GotBureauBACS_Right) && !v_AppendToFile && _parent.v_GlobalBACS_Type == "Q") 
                            {
                                new OutputQ_typeHeader(this).Run();
                            }
                            // ED/547 && PBI 32779
                            if (!v_AppendToFile && u.FileExist(u.Trim(v_BACS_FilenameLocal)) && !Tempest.Roles.Elan.Allowed && !_parent.v_TieredBacsFunctionality)
                            {
                                u.FileDelete(u.Trim(v_BACS_FilenameLocal));
