                    private string GetClientLineEQL(EQLParams clientLine)
                    {
                        DateTime lastMonth = DateTime.Now.AddMonths(-1);
                        List<string> cols = new();
                        cols.Add(TruncateString("C", 1));
                        cols.Add(TruncateString(clientLine.PaymentAmount, 18));
                        cols.Add(TruncateString(clientLine.BeneficiaryName, 18));
                        cols.Add(TruncateString(clientLine.BeneficiaryAccountNo, 8));
                        cols.Add(TruncateString(clientLine.BeneficiarySortCode, 6));
                        cols.Add(TruncateString($@"Salaries - { lastMonth.ToString("MMM") } '{ lastMonth.ToString("yy") }", 18));

                        return string.Join(",", cols.Select(s => s.Trim()));
                    }

