                    private string TruncateString(string val, int length)
                    {
                        val = val.Trim();
                        if (!val.IsNullOrWhiteSpace() && val.Length > length)
                        {
                            return val.Substring(0, length);
                        }

                        return val;
                    }

                    protected void TruncateHeader(ref List<string> csvColumns) 
                    {
                        if (csvColumns[8].Length > 18) 
                        {  
                            csvColumns[8] = csvColumns[8].Substring(0, 18);
                        }
                        if (csvColumns[12].Length > 34)
                        {
                            csvColumns[12] = csvColumns[12].Substring(0, 34);
                        }                       
                        if (csvColumns[36].Length > 18)
                        {
                            csvColumns[36] = csvColumns[36].Substring(0, 18);
                        }                        
                    }

                    protected void TruncateDetail(ref List<string> csvColumns)
                    {
                        if (csvColumns[16].Length > 15)
