                        if (csvColumns[24].Length > 6)
                        {
                            csvColumns[24] = csvColumns[24].Substring(0, 6);
                        }
                        if (csvColumns[30].Length > 8)
                        {
                            csvColumns[30] = csvColumns[30].Substring(0, 8);
                        }
                        if (csvColumns[32].Length > 35)
                        {
                            csvColumns[32] = csvColumns[32].Substring(0, 35);
                        }
                        if (csvColumns[36].Length > 18)
                        {
                            csvColumns[36] = csvColumns[36].Substring(0, 18);
                        }                        
                    }

