                    void InitializeDataView()
                    {
                        From = EmployeeSessionHist;

                        #region Relations

                        Relations.Add(PersStaticPayroll,
                                PersStaticPayroll.System_ID.IsEqualTo(EmployeeSessionHist.System_ID),
                            PersStaticPayroll.SortByBySystemID_PK);

                        Relations.Add(PersJobDetails1,
                                PersJobDetails1.System_ID.IsEqualTo(EmployeeSessionHist.System_ID),
                            PersJobDetails1.SortByBySystemID_PK);

                        Relations.Add(BC_InternationalBankAccounts,
                                BC_InternationalBankAccounts.Bank_Address_Code.IsEqualTo(PersStaticPayroll.Ben_Bank_Address_Code),
                            BC_InternationalBankAccounts.SortByByBankAddressCode);

                        Relations.Add(BC_InternationalBankAccounts1,
                                BC_InternationalBankAccounts1.Bank_Address_Code.IsEqualTo(PersStaticPayroll.Int_Bank_Address_Code),
                            BC_InternationalBankAccounts1.SortByByBankAddressCode);

                        Relations.Add(PersStaticPersonnel, RelationType.OuterJoin,
                                PersStaticPersonnel.System_ID.IsEqualTo(EmployeeSessionHist.System_ID),
                            PersStaticPersonnel.SortByBySystemID_PK);

                        Relations.Add(vw_Sub_Contractor_Certs,
                                vw_Sub_Contractor_Certs.Employer_Ref.IsEqualTo(_parent._parent.v_EmployerReference).And(
                                vw_Sub_Contractor_Certs.Personnel_Ref.IsEqualTo(EmployeeSessionHist.Personnel_Ref)).And(
                                vw_Sub_Contractor_Certs.Current_Or_Previous.IsEqualTo(true)));
                        Relations[vw_Sub_Contractor_Certs].BindEnabled(() => PersStaticPersonnel.Employment_Type == "U" || PersStaticPersonnel.Employment_Type == "M");

                        Relations.Add(Currency_Bank_Details,
                                Currency_Bank_Details.Employer_Ref.IsEqualTo(_parent._parent.v_EmployerReference).And(
                                Currency_Bank_Details.Currency_Code.IsEqualTo(PersStaticPersonnel.Worker_Currency_Code)),
                            Currency_Bank_Details.SortByByEmp_Currency_PK);

                        Relations.Add(BankDetails,
                                BankDetails.Bank_Sort_Code.IsEqualTo(PersStaticPayroll.Bank1_Sort_Code),
                            BankDetails.SortByByBankSortCode_PK);

                        Relations.Add(BankDetails1,
                                BankDetails1.Bank_Sort_Code.IsEqualTo(PersStaticPayroll.Bank2_Sort_Code),
                            BankDetails1.SortByByBankSortCode_PK);

                        Relations.Add(vw_Sub_Contractor_Certs1,
                                vw_Sub_Contractor_Certs1.Employer_Ref.IsEqualTo(EmployeeSessionHist.Employer_Ref).And(
                                vw_Sub_Contractor_Certs1.Personnel_Ref.IsEqualTo(PersStaticPersonnel.Personnel_Ref)),
                            vw_Sub_Contractor_Certs1.SortByByNo_AndCurrent);
                        Relations[vw_Sub_Contractor_Certs1].NotifyRowWasFoundTo(RC153);

                        Relations.Add(AM_Managed_Agencies,
                                AM_Managed_Agencies.Employer_Ref.IsEqualTo(EmployeeSessionHist.Employer_Ref).And(
                                AM_Managed_Agencies.Purchase_Ledger_Account_Num.IsEqualTo(vw_Sub_Contractor_Certs1.Purchase_Ledger_Account_Num)),
                            AM_Managed_Agencies.SortByPK_Managed_Agencies);
                        Relations[AM_Managed_Agencies].BindEnabled(() => _parent._parent._parent.Alex_Mann);
                        Relations[AM_Managed_Agencies].NotifyRowWasFoundTo(ManagedAgency);

                        Relations.Add(ValidTimesheets,
                                ValidTimesheets.Tax_Yr_Proc_By_Payroll.IsEqualTo(EmployeeSessionHist.Tax_Year).And(
                                ValidTimesheets.Period_Proc_By_Payroll.IsEqualTo(EmployeeSessionHist.Tax_Period)).And(
                                ValidTimesheets.Session_Proc_By_Payroll.IsEqualTo(EmployeeSessionHist.Tax_Session)).And(
