                    }
                    protected override void OnLeaveRow()
                    {
                        if ((EmployeeSessionHist.Pay_Method1_Amount != 0 && EmployeeSessionHist.Pay_Method1 == "BACS" || EmployeeSessionHist.Pay_Method2_Amount != 0 && EmployeeSessionHist.Pay_Method2 == "BACS") && u.Not(ManagedAgency) && Tempest.Roles.TempestAsA_service.Allowed && _parent._parent.v_BACS_ProcessingOwner == "S" && _parent._parent.v_Division == PersJobDetails1.Division)
                        {
                            _parent._parent.v_CountOfEmployees.Value++;
                        }

                        if (Exp_81())
                        {

                            {

                                _parent._parent.v_Counter.Value++;
                                if (_parent._parent.v_Counter == v_Seed)
                                {
                                    _parent._parent.v_Counter.Value = -(v_Seed);
                                }
                                Application.v_NumberProcessed.Value = Application.v_NumberProcessed + 1;
                                Application.v_TimingPeriod.Value = u.mTime() - Application.v_PeriodStartTimeMsecs;

                                if (_parent._parent.v_EndCurrencyProcessing)
                                {
                                    // Process UI when Elapsed Time>1 sec OR Refresh Time Interval reached
                                    if (Application.v_TimingPeriod >= Application.v_BatchRefreshIntervalSecs * 1000 || Application.v_TimingPeriod > 1000)
                                    {
                                        Cached<Part007._GenericBatchWindow>().Run(
                                                        Exp_55(),
                                                        Exp_56(),
                                                        v_Seed,
                                                        "D");
                                    }
                                }
                                else
                                {
                                    _parent.v_Work_Legend.Value = Exp_56();
                                    _parent.v_Work_Proc.Value++;
                                }

                                // XY1408 to pick up company name if Limited subcontractor
                                v_BACS_name.Value = u.Trim(EmployeeSessionHist.Bank_Account_Name_1);
                                if (RC153 == true && vw_Sub_Contractor_Certs1.Business_Name != "" && vw_Sub_Contractor_Certs1.Indiv_Or_Ltd_Co_Or_Partner == "L" && u.Trim(_parent._parent.v_BACS_TypeLocalTask) != "E" && u.Trim(_parent._parent.v_BACS_TypeLocalTask) != "A")
                                {
                                    v_BACS_name.Value = u.Trim(vw_Sub_Contractor_Certs1.Business_Name);
                                }

                                // Pay Method 1 = BACS
                                if (EmployeeSessionHist.Pay_Method1 == "BACS" && EmployeeSessionHist.Pay_Method1_Amount > 0)
                                {
                                    if (u.Trim(_parent._parent.v_BACS_TypeLocalTask) == "HI")
                                    {
                                        _viewBACS_DETAIL_PayMeth1_HI.WriteTo(_ioBACS_ASCII_File);
                                        CONTRA_Total.Value += EmployeeSessionHist.Pay_Method1_Amount;
                                        UTL1_Cnt.Value++;
                                    }

                                    _parent._parent.v_TotalAmount.Value += EmployeeSessionHist.Pay_Method1_Amount;
                                    if (EmployeeSessionHist.Pay_Method1_Amount != 0 && EmployeeSessionHist.Pay_Method1 == "BACS")
                                    {
                                        v_NumberOfLinesOutput.Value++;
                                    }
                                }


                                if (_parent._parent.v_BACS_TypeLocalTask != "Q" || _parent._parent.v_BACS_TypeLocalTask == "Q" && v_NumberOfLinesOutput < 99)
                                {
                                    // Pay Method 2 = BACS
                                    if (EmployeeSessionHist.Pay_Method2 == "BACS" && EmployeeSessionHist.Pay_Method2_Amount > 0)
                                    {
