                            if (EmployeeSessionHist.Pay_Method1_Amount != 0 && EmployeeSessionHist.Pay_Method1 == "BACS" || EmployeeSessionHist.Pay_Method2_Amount != 0 && EmployeeSessionHist.Pay_Method2 == "BACS")
