                                if (PersStaticPersonnel.Alert_By_SMS && PersStaticPersonnel.Mobile_Telephone != "" && _parent._parent._parent.v_UseSMS_Triggers && _parent._parent.RC_SMS_TriggerExists && _parent._parent.v_SendEmail_SMS_Messages)
                                {
