                Exit(ExitTiming.AfterRow);
                RowLocking = LockingStrategy.OnRowLoading;
                TransactionScope = TransactionScopes.Task;
                Activity = Activities.Browse;
                KeepViewVisibleAfterExit = true;
            }
            protected override void OnLeaveRow()
            {
                _parent.v_GlobalBACS_Type.Value = SaPHuR_control.BACS_Type;
                _parent.v_GlobalBACS_InterfaceFile.Value = SaPHuR_control.BACS_Interface_File;

                _parent.v_TieredBacsFunctionality.Value = TempestCore.GetFSwitch.Val("TIEREDBACS");
                if (_parent.v_TieredBacsFunctionality && SaPHuR_control.BACS_Type_2 != "" && SaPHuR_control.BACS_Type_2_Minimum_Records > 0)
                {
                    _parent.v_GlobalBACS_Type_2.Value = SaPHuR_control.BACS_Type_2;
                    _parent.v_GlobalBACS_InterfaceFile_2.Value = SaPHuR_control.BACS_Interface_File_2;
                    _parent.v_GlobalBACS_Type_2_Minimum_Records.Value = SaPHuR_control.BACS_Type_2_Minimum_Records;
                    _parent.v_Default_Tiered_BACS_Beneficiary_ID.Value = TempestControlFile2.Alpha_Value;
                }
                else
                {
                    _parent.v_GlobalBACS_Type_2.Value = "";
                    _parent.v_GlobalBACS_InterfaceFile_2.Value = "";
                    _parent.v_GlobalBACS_Type_2_Minimum_Records.Value = 0;
                    _parent.v_Default_Tiered_BACS_Beneficiary_ID.Value = "";
                }

                // R8564...
                _parent.v_NumberOfLinesInFasterPay.Value = TempestControlFile.Numeric_Value;
            }
        }
